mapper.select:
select count(*) from water_intake
where time_start = to_char(sysdate, 'yyyy-mm-dd')
and user_id = #{id}

mapper.update:
update water_intake 
set title = title + #{title}
where time_start = to_char(sysdate, 'yyyy-mm-dd')
and user_id = #{user_id}

mapper.delete:
delete water_intake
where time_start = to_char(sysdate, 'yyyy-mm-dd')
and user_id = #{id}
