mapper.select:
	 SELECT SPORTS_NAME, CAL, SPORTS_IMG
	 FROM SPORTS

 	where sports_name = #{sports}

	select * from
	(select rownum rnum, pm_no, sports_name, pm_kcal, sports_img ,user_id
	from PERSONAL_MANAGEMENT
	where to_char(pm_date, 'YYYYMMDD') = to_char(sysdate, 'YYYYMMDD')
	and USER_ID = #{id}
	order by pm_date
	)
	where

  		rnum >= #{start} 
  	 AND
  	 	rnum <= #{end}
